# \brief Provides the function for reading inputs and the function for checking the physical constraints on the input
# \note Generated by Drasil v0.1-alpha

import math

## \brief Reads input from a file with the given file name
# \param filename name of the input file
# \return launch speed (m/s)
# \return launch angle (rad)
# \return target position (m)
def get_input(filename):
    infile = open(filename, "r")
    infile.readline()
    v_launch = float(infile.readline())
    infile.readline()
    theta = float(infile.readline())
    infile.readline()
    p_target = float(infile.readline())
    infile.close()
    
    return v_launch, theta, p_target

## \brief Verifies that input values satisfy the physical constraints
# \param v_launch launch speed (m/s)
# \param theta launch angle (rad)
# \param p_target target position (m)
def input_constraints(v_launch, theta, p_target):
    if not(v_launch > 0.0):
        print("Warning: ", end="")
        print("v_launch has value ", end="")
        print(v_launch, end="")
        print(", but is suggested to be ", end="")
        print("above ", end="")
        print(0.0, end="")
        print(".")
    if not(0.0 < theta and theta < math.pi / 2.0):
        print("Warning: ", end="")
        print("theta has value ", end="")
        print(theta, end="")
        print(", but is suggested to be ", end="")
        print("between ", end="")
        print(0.0, end="")
        print(" and ", end="")
        print(math.pi / 2.0, end="")
        print(" ((pi)/(2))", end="")
        print(".")
    if not(p_target > 0.0):
        print("Warning: ", end="")
        print("p_target has value ", end="")
        print(p_target, end="")
        print(", but is suggested to be ", end="")
        print("above ", end="")
        print(0.0, end="")
        print(".")
